mapper.select:

			select r.*, c.checkinId, c.userName, c.carPlate, c.maintainOrFix, cpy.companyId, cpy.companyName
			  from t_maintain_record r,  t_checkin c, t_company cpy
			  where c.checkinId = r.checkinId and c.companyId = cpy.companyId and r.recordStatus = #{status}

		order by r.recordCreatedTime desc, r.companyId
        limit #{pager.beginIndex}, #{pager.pageSize}


			select r.*, c.checkinId, c.userName, c.carPlate, c.maintainOrFix, cpy.companyId, cpy.companyName
			  from t_maintain_record r,  t_checkin c, t_company cpy
		      where 1= 1 and c.checkinId = r.checkinId and c.companyId = cpy.companyId


            and c.userName like concat('%',#{record.checkin.userName},'%')


            and c.userPhone like concat('%',#{record.checkin.userPhone},'%')


            and r.startTime = #{record.startTime}


            and c.maintainOrFix = #{record.checkin.maintainOrFix}


            and r.companyId = #{record.companyId}

        order by r.recordCreatedTime desc, r.companyId
        limit #{pager.beginIndex}, #{pager.pageSize}


			select count(*) from t_maintain_record r, t_checkin c where 1 = 1 and r.checkinId = c.checkinId


            and c.userName like concat('%',#{checkin.userName},'%')


            and c.userPhone like concat('%',#{checkin.userPhone},'%')


            and r.startTime = #{startTime}


            and c.maintainOrFix = #{checkin.maintainOrFix}


            and r.companyId = #{companyId}
