mapper.select:

  select * from t_maintain_remind rem, t_maintain_record red, t_checkin c where rem.userId=c.userId and c.checkinId = red.checkinId and red.recordStatus ="Y"


	and rem.companyId = #{user.companyId}

order by rem.companyId desc, rem.remindCreatedTime desc, red.recordStatus desc
limit #{pager.beginIndex}, #{pager.pageSize}


select r.*, c.checkinId, c.userName, c.carPlate, c.carMileage, c.userPhone, c.maintainOrFix, cpy.companyId, cpy.companyName
	  from t_maintain_remind r,  t_checkin c, t_company cpy
      where 1 = 1 and c.userId = r.userId and c.companyId = cpy.companyId


	and r.companyId = #{user.companyId}


	and c.userName like concat('%',#{remind.checkin.userName},'%')


	and r.remindType = #{remind.remindType}

order by c.companyId desc, r.remindCreatedTime desc,
limit #{pager.beginIndex}, #{pager.pageSize}
